(function iframePorthole(){	var portholeMethodOn = window.addEventListener ? "addEventListener" : "attachEvent";	var portholeMethodOff = window.addEventListener ? "removeEventListener" : "detachEvent";	var portholeEvent = portholeMethodOn == "attachEvent" ? "onmessage" : "message";		function portholeSetup(e) {		console.log(e);				// go to new page within this project		if (e.data.page) {			location.pathname = e.data.page;		}		// go to new project		else if (e.data.url) {			location.href = e.data.url;		}		// login info, try to prefill as much as we can		else if (e.data.user) {			$('#fld_user').val(e.data.user);			$('#fld_pass').val(e.data.pass);						// we have a user and a pass, try to log in			if (e.data.user && e.data.pass) {				$('#btn_signin').click();			}			// focus password if we don't have it			else if ($('#fld_user').val().length) {				$('#fld_pass').focus();			}		}	}		window[portholeMethodOn](portholeEvent,portholeSetup,false);})();